#include<iostream>
using namespace std;
int main()
{
  
    //Formula SI = PRT/100
    //SI = simple Interest
     //P = Principle Amount
     //R = Rate of Interest in percent
     //T = time duration
   float p , r, t , SI;
   cout<<"Enter the value of P , R , T = "<<endl;
   cin>>p>>r>>t;
   SI = (p*r*t)/100;
   cout<<"Simple Interest = "<<SI;




}